mapper.select:
SELECT * FROM BOARD_IMAGE
WHERE BOARD_NO = #{boardNo}
ORDER BY BOARD_IMAGE_ORDER

SELECT COUNT(*) FROM BOARD_LIKE
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE 
FROM "BOARD_LIKE"
WHERE MEMBER_NO  = #{memberNo}
AND BOARD_NO =  #{boardNo}

mapper.update:
UPDATE "BOARD" SET
BOARD_COUNT = BOARD_COUNT +1
WHERE BOARD_NO = #{boardNo}
