mapper.select:
SELECT ARTIST_GROUP_NO, ARTIST_GROUP_MAINIMG, ARTIST_GROUP_LOGOIMG, ARTIST_GROUP_INTRODUCTION, ARTIST_GROUP_TITLE
FROM ARTIST_GROUP ag 
WHERE ARTIST_GROUP_TITLE = #{artistGroupTitle}

SELECT * FROM BOARD_IMAGE
WHERE BOARD_NO = #{boardNo}
ORDER BY BOARD_IMAGE_ORDER

SELECT COUNT(*) FROM BOARD_LIKE
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE 
FROM "BOARD_LIKE"
WHERE MEMBER_NO  = #{memberNo}
AND BOARD_NO =  #{boardNo}
