mapper.insert:
INSERT INTO MEDIA_COMMENT MC VALUES(SEQ_MESSAGE_COMMENT_NO.NEXTVAL
, #{mediaCommentContent}, SYSDATE,
DEFAULT, #{mediaNo}, 3, 

	NULL


	#{mediaParentCommentNo}

)

mapper.select:
SELECT MEDIA_COMMENT_NO ,MEDIA_COMMENT_CONTENT , TO_CHAR(MEDIA_COMMENT_ENROLL_DATE, 'MM.DD. HH24:MI') AS MEDIA_COMMENT_ENROLL_DATE, MEMBER_NO , (SELECT MEMBER_NICKNAME FROM "MEMBER" M WHERE M.MEMBER_NO = MC.MEMBER_NO ) AS MEMBER_NICKNAME
FROM MEDIA_COMMENT MC WHERE MEDIA_NO = #{mediaNo}
AND MEDIA_PARENT_COMMENT_NO IS NULL
AND MEDIA_PARENT_COMMENT_NO = #{mediaParentCommentNo}
ORDER BY MC.MEDIA_COMMENT_ENROLL_DATE DESC
