mapper.select:
SELECT COUNT(*)
FROM ARTIST_SUBSCRIBE
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE BOARD SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_NO = #{boardNo}

UPDATE MEMBER SET
MEMBER_PROFILE = #{memberProfile}
WHERE MEMBER_NO =#{memberNo}

UPDATE MEMBER SET
MEMBER_BACK_IMAGE = #{memberBackImage}
WHERE MEMBER_NO =#{memberNo}
